import React from "react";

const TypeWriter = ({ titulo, setTitulo }) => {
  function typeWriter(titulo) {
    const textoArray = titulo.innerHTML.split("");
    titulo.innerHTML = "";
    textoArray.forEach((letra, i) => {
      setTimeout(() => (titulo.innerHTML += letra), 75 * i);
    });
  }

  function mudarTitulo(event) {
    const texto = (titulo.innerText = "oi");
    setTitulo(texto);
  }

  return <button onClick={() => mudarTitulo()}>{titulo}OI</button>;
};

export default TypeWriter;
