import React from "react";

function App() {
  const [contar, setContar] = React.useState(1);
  const [Item, setItem] = React.useState("Item 1");

  function handleClick() {
    setContar(contar + 1);
    setItem("Item {contar}");
  }

  return (
    <button onClick={handleClick}>
      {contar}
      {Item}
    </button>
  );
}

export default App;
