import React from "react";

function App() {
  const [contar, setContar] = React.useState(1);
  const [Item, setItem] = React.useState("Item 1");

  function handleClick() {
    setContar((contar) => {
      return contar + 1;
    });
  }

  return (
    <button onClick={handleClick}>
      {contar},{Item}
    </button>
  );
}

export default App;
