import React from "react";
import { GlobalContext } from "./GlobalContext";

const Produto = () => {
  const global = React.useContext(GlobalContext);
  const [produto, setProduto] = React.useState(null);

  React.useEffect(() => {
    setProduto(global.nome);
  });

  return (
    <div>
      <div>
        <h2>Produto</h2>
        <p>{global.id}</p>
      </div>
    </div>
  );
};

export default Produto;
